import { useEffect, useState } from "react";
import Card from '../Card'

const md5 = require('md5')
const PRIVATE_KEY = '1a79518320092f0adcca9822825517090a22e0fc'
const PUBLIC_KEY = 'e8e61be1a12aa4da11480c2bb40cbbff'
const TS = Date.now()

function PersonajesMarvel(){
    const [personaje, setPersonajes] = useState([]);

    let hash = md5(`${TS}${PRIVATE_KEY}${PUBLIC_KEY}`)
    let URL = `https://gateway.marvel.com:443/v1/public/characters?ts=${TS}&apikey=${PUBLIC_KEY}&hash=${hash}`

    useEffect(()=>{
        if(!navigator.onLine){
            if(localStorage.getItem("personaje") === null){
                setPersonajes("Loading...")
            } else {
                setPersonajes(localStorage.getItem("personaje"))
            }
            
        } else {
            fetch(URL).then(res=>res.json()).then(res=>{
                console.log("Res",res);
                localStorage.setItem("personaje", res.value)
                setPersonajes(res.value);
            })
        }        
    },);

    return(
        <div className="container-fluid">
            {personaje.map((character) => (
                <Card key = {character.id} character = {character} />
            ))}
        </div>
    )
}

export default PersonajesMarvel;
